def solution(n):        
    operationCount = 0
    n = int(n)

    while n > 1:
        if n % 2 == 0:             # bitmask: *0
            n = n // 2
        elif n == 3 or n % 4 == 1: # bitmask: 01
            n = n - 1
        else:                      # bitmask: 11
            n = n + 1
        
        operationCount += 1

    return operationCount
    


        
        




n = ''
n = '15'
#n = '100'
